descriptor.controllerScript:
let $scope;

let decimalChar;

// De-ref function for variabel/value lookup
// Check if the variable exists
self.variableExists = function(tag)
{   
    var varName = self.ctx.settings[tag];
    var retVal = false;
    if (varName === undefined) varName = tag;

    // Scan throug all data to find the correct variable name
    for (i = 0; (!retVal) && (i < self.ctx.data.length); i++)
    {
        retVal = (self.ctx.data[i].dataKey.name === varName);
    }

    return retVal;
}


// De-ref function for variabel/value lookup
// Check if the variable is referenced in the setting
// If not in the settiongs, assume that the name can be directly used
self.getValue = function(tag, missing)
{   
    var varName = self.ctx.settings[tag];
    var retVal = missing;
    if (varName === undefined) varName = tag;

    // Scan throug all data to find the correct variable name
    for (i = 0; i < self.ctx.data.length; i++)
    {
        data = self.ctx.data[i];
        if (data.dataKey.name === varName) retVal = data.data[0][1];
    }

    return retVal;
}

