descriptor.controllerScript:
let $scope;
let settings;
let attributeService;
let toast;
let utils;
let types;
let decimalChar;

self.onInit = function() {

    $scope = self.ctx.$scope;
    attributeService = $scope.$injector.get('attributeService');
    toast = $scope.$injector.get('toast');
    utils = $scope.$injector.get('utils');
    types = $scope.$injector.get('types');
    settings = angular.copy(self.ctx.settings) || {};
    decimalChar = (1.5).toLocaleString().charAt(1);

    $scope.settings = settings;
    $scope.isValidParameter = true;
    $scope.dataKeyDetected = false;
    $scope.message = 'No entity selected';
    $scope.requiredErrorMessage = settings.requiredErrorMessage || "Entity attribute is required";

// Init scope variables
    $scope.inputEnabled = self.ctx.settings.inputEnabled;
    $scope._value_old = $scope._value;
    $scope._unit = self.ctx.units;

    if (self.ctx.datasources && self.ctx.datasources.length) 
    {
        var datasource = self.ctx.datasources[0];

        if (datasource.type === 'entity') 
        {
            if (datasource.entityType === "DEVICE") 
            {
                if (datasource.entityType && datasource.entityId) 
                {
                    $scope.entityDetected = true;
                }
            } else 
            {
                $scope.message = 'Selected entity cannot have shared attributes';
            }
        }
        if (datasource.dataKeys.length) 
        {
            if (datasource.dataKeys[0].type != "attribute") 
            { 
                $scope.isValidParameter = false;
            } else 
            {
                $scope.currentKey = datasource.dataKeys[0].name;
                $scope.dataKeyType = datasource.dataKeys[0].type;
                $scope.dataKeyDetected = true;
            }
        }
    }


    if (self.ctx.datasources.length > 0) 
    {
        var tbDatasource = self.ctx.datasources[0];
        var datasourceId = 'tbDatasource' + 0;

        self.ctx.datasourceContainer = $('#tbDatasource1', self.ctx.$container);


        if (tbDatasource.dataKeys.length > 0) 
        {
            var dataKey = tbDatasource.dataKeys[0];

            if (self.ctx.settings.labelVisible) self.ctx.$scope._labelText = dataKey.label;
            else self.ctx.$scope._labelText = "";
            self.ctx.$scope._txtValue = '0';
            self.ctx.$scope._txtValue = self.ctx.units;
            self.ctx.$scope._labelStyle = 'font-size: 12px;';
            self.ctx.$scope._textStyle = 'font-size: 12px;';
        }
    }

    $.fn.textWidth = function() {
        var html_org = $(this).html();
        var html_calc = '<span>' + html_org +
            '</span>';
        $(this).html(html_calc);
        var width = $(this).find('span:first')
            .width();
        $(this).html(html_org);
        return width;
    };

    // Restore all original values when user clicks "X"
    $scope.restoreValues = function() {
        $scope._value = $scope._value_old;

        $scope.isFocused = false;
    };

    // Check if any values changed. Return TRUE if any changes.
    $scope.checkChangedValues = function() 
    {
        check = ($scope._value === $scope._value_old)

        return check;
    }

    $scope.changeFocus = function() 
    {
        if ($scope._value === $scope._value_old) 
        {
            $scope.isFocused = false;
        }
    }


    $scope.updateAttribute = function() 
    {
        if ($scope.entityDetected) 
        {
            var datasource = self.ctx.datasources[0];

            // Write value to attribute
            attributeService.saveEntityAttributes(
                datasource.entityType,
                datasource.entityId,
                types.attributesScope.shared
                .value,
                [{
                    key: $scope.currentKey,
                    value: $scope._value
                }]
            ).then(
                function success() {

                    // Update the HTML "previous" values to the new ones
                    $scope._value_old = $scope._value;

                    if (settings
                        .showResultMessage) {
                        toast.showSuccess(
                            'Update successful',
                            1000, angular
                            .element(self
                                .ctx
                                .$container
                                ),
                            'bottom left');
                    }
                },
                function fail() {
console.log("Fail")

                    if (settings.showResultMessage) {
                        toast.showError('Update failed',
                            angular.element(
                                self.ctx
                                .$container),'bottom left');
                    }
                }
            );
        }
    };


    self.onResize();
};


self.onDataUpdated = function() {
/*
    if (!$scope.isFocused) {
        if (self.ctx.data.length > 0) {
            var cellData = self.ctx.data[0];
            if (cellData.data.length > 0) {
                var value = cellData.data[cellData.data.length - 1][1]
                $scope._value = value;
            }
        }
    }
*/

    function isNumber(n) {
        return !isNaN(parseFloat(n)) && isFinite(n);
    }

    //    if (self.ctx.valueCell && self.ctx.data.length > 0) {

    if (!$scope.isFocused) 
    {
        if (self.ctx.data.length > 0) 
        {
            var cellData = self.ctx.data[0];
            if (cellData.data.length > 0) 
            {
                var tvPair = cellData.data[cellData.data.length - 1];
                var value = tvPair[1];
                var txtValue;
                if (isNumber(value)) 
                {
                    var decimals = self.ctx.decimals;
                    var units = self.ctx.units;
                    if (self.ctx.datasources.length > 0 &&
                        self.ctx.datasources[0].dataKeys.length > 0) 
                        {
                            dataKey = self.ctx.datasources[0].dataKeys[0];
                        if (dataKey.decimals || dataKey.decimals === 0) 
                        {
                            decimals = dataKey.decimals;
                        }
                        if (dataKey.units) 
                        {
                            units = dataKey.units;
                        }
                    }

                    txtValue = self.ctx.utils.formatValue(value, decimals, '', true).toLocaleString().replace('.', decimalChar);

                } else 
                {
                    txtValue = value;
                }
                $scope._value = txtValue;

            }
        }

        self.ctx.$scope.$digest();
    }
};


self.onResize = function() 
{

    self.ctx.$scope._labelStyle = "";
    self.ctx.$scope._textStyle  = "";

// Scale font size basded on ctx.height if dynamic scaling ins enabled
    if (self.ctx.settings.scaleDynamic)
    {
        var labelFontSize;
            self.ctx.padding = self.ctx.height / 20;
            labelFontSize = self.ctx.height / 1.7;
            self.ctx.valueFontSize = self.ctx.height / 1.7;

        self.ctx.padding = Math.min(12, self.ctx.padding);

        self.ctx.$scope._labelStyle = 'font-size: ' + labelFontSize + 'px; padding: ' + self.ctx.padding + 'px;'
        self.ctx.$scope._textStyle = 'font-size: ' + self.ctx.valueFontSize + 'px; padding: ' + self.ctx.padding + 'px;'
    }

// Add CSS inpuit from settings pane
    self.ctx.$scope._labelStyle += self.ctx.settings.labelStyle;
    self.ctx.$scope._textStyle += self.ctx.settings.valueStyle;

};

self.typeParameters = function() {
    return {
        maxDatasources: 1,
        maxDataKeys: 1
    };
};


self.onDestroy = function() {};

descriptor.settingsSchema:
{
    "schema": {
        "type": "object",
        "title": "Settings",
        "properties": {
            "inputEnabled" : {
                "title" : "Input enabled",
                "type": "boolean",
                "default" : true
            },
            "labelVisible" : {
                "title" : "Display label",
                "type": "boolean",
                "default" : true
            },
            "scaleDynamic" : {
                "title" : "Dynamic font scaling",
                "type": "boolean",
                "default" : true
            },
            "labelStyle":{
                "title":"Label styling (CSS). Note: this may override dynamic scaling!",
                "type":"string"
            },
            "valueStyle":{
                "title":"Value styling (CSS). Note: this may override dynamic scaling!",
                "type":"string"
            }
        },
        "required": []
    },
    "form": [
        {
            "key":"inputEnabled"
        },
        {
            "key" : "labelVisible"
        },
        {
            "key":"scaleDynamic"  
        },
        {
            "key":"labelStyle"   
        },
        {
            "key":"valueStyle"   
        }
    ]
}
